<!doctype html><html lang="en"><head>  <meta charset="utf-8">  <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">  <meta name="description" content="">  <meta name="author" content="Gabriel Medina Vindigni">  <title>Soporte Administrativo</title>  <link href="Include/css/bootstrap.css" rel="stylesheet">  <link href="Include/css/login.css" rel="stylesheet">  <style type="text/css">  .nuevoRegistro{    cursor: pointer;  }</style><link rel="stylesheet" href="Include/alertifyjs/css/alertify.css"><script src="Include/js/jquery-3.3.1.min.js"></script><script src="Include/alertifyjs/alertify.js"></script></head><body>  <div class="container text-center">    <form class="form-signin">      <img class="mb-2" src="Include/img/logo2.png" alt="" width="72" height="72">      <p class="text-dark">Gerencia Soporte Administrativo</p>      <h1 class="h3 mb-3 font-weight-normal">Inicio de sesión</h1>      <label for="inputEmail" class="sr-only"></label>      <input type="text" id="user" class="form-control" placeholder="Usuario de red" required autofocus>      <div class="mt-2"></div>      <label for="inputPassword" class="sr-only"></label>      <input type="password" id="pwd" class="form-control" placeholder="Introduce tu contraseña" required>    </form>      <div class="row justify-content-center">      <button class="btn btn-lg btn-Secondary entrar" >Iniciar</button>      </div>           <a class="nuevoRegistro" data-toggle="modal" data-target="#nuevoRegistro">¿No está registrado?</a>      <p class="mt-4 mb-3 text-muted">&copy; 2018 - Soporte Administrativo</p>    <div class="modal fade" id="nuevoRegistro" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">      <div class="modal-dialog" role="document">        <div class="modal-content modal-sm mx-auto mt-5">          <div class="modal-header">            <h5 class="modal-title" id="exampleModalLabel">Nuevo Usuario</h5>            <button type="button" class="close" data-dismiss="modal" aria-label="Close">              <span aria-hidden="true">&times;</span>            </button>          </div>          <div class="modal-body">            <form>              <div class="form-group">                <input type="text" class="form-control" id="name1" placeholder="Ingrese su nombre">                <input type="text" class="form-control mt-1" id="name2" placeholder="Ingrese sus apellidos">              </div>              <div class="form-group">                <input type="text" class="form-control" id="nuevoUser" placeholder="Ingrese su usuario de red">              </div>              <div class="form-group">                <input type="password" class="form-control" id="pwd1" placeholder="Ingrese su contraseña">                <input type="password" class="form-control mt-1" id="pwd2" placeholder="Confirme su contraseña">              </div>            </form>              <button class="btn btn-Secondary aceptar">Aceptar</button>                      </div>        </div>      </div>    </div>  </div></body></html><script src="Include/js/bootstrap.min.js"></script><script type="text/javascript">  $(document).ready(function(){    $('.entrar').click(function(){      var user = $('#user').val();      var pwd = $('#pwd').val();      entrar = "userEntrar="+user+            "&pwd="+pwd;      if((user == '')||(pwd == '')) {        alertify.error("Error en Usuario y/o Contraseña.");      }else{        $.ajax({        data:  entrar,           url:   'Models/GestionUsers.php',           type:  'post',          success: function (data, textStatus) {             switch(data) {                case "3":                                       window.location="views/";                    $('#pwd').val('');                    $('#user').val('');                    break;                case "2":                    alertify.error("Usuario y/o Contraseña invalidos.");                    $('#pwd').val('');                    $('#user').val('');                    break;                                        default:                     alertify.alert('Soporte Adminitrativo - Indicadores', 'El usuario ' + data + ' no ha sido autorizado por el administrador.', function(){ alertify.error('Debe notificar al administrador del sistema.'); });                    $('#pwd').val('');                    $('#user').val('');            };        },      });      };    });  });</script><script type="text/javascript"> $('.aceptar').click(function(){  var user = $('#nuevoUser').val();  var pwd1 = $('#pwd1').val();  var pwd2 = $('#pwd2').val();  if(($('#name1').val() != '') && ($('#name2').val() != '') ){    var name = $('#name1').val() + ' ' + $('#name2').val();    if(user == ''){      alertify.error("Rellene el campo User");    }else if((pwd1 != pwd2) || (pwd1 == '') || (pwd2 == '') ) {      alertify.error("Error en contraseña. No coincide");    }else{      $.ajax({        data:  {userRegistro : user,          pwd : pwd1,          name : name},           url:   'Models/GestionUsers.php',           type:  'post',          success: function (data, textStatus) {            switch(data) {                case "1":                                       $('#nuevoRegistro').modal('hide');                    alertify.success("Usuario registrado con Éxito.");                    break;                case "0":                    break;                                        default:                     $('#nuevoRegistro').modal('hide');                    alertify.error('Error. Ya existe el usuario "'+ data + '"');            }        },      });            }  }else{    alertify.error("Error. Complete Nombre y Apellidos");  }}); $('.nuevoRegistro').click(function(){  $('#nuevoUser').val('');  $('#pwd1').val('');  $('#pwd2').val('');  $('#name1').val('');  $('#name2').val('');});</script>